    },
    price: {
        fontSize: 13,
        textTransform: 'uppercase',
        fontWeight: '600',
    },
    buttonContainer: {
        flex: 1,
        alignItems: 'flex-end',
        height: 80,
        justifyContent: 'space-evenly'
    },
    plusMinusContainer: {
        flexDirection: 'row',
    },
    button: {
        width: 20,
        height: 20,
        alignItems: 'center'
    },
    numOfProducts: {
        fontSize: 13,
        textTransform: 'uppercase',
        fontWeight: '600',
    }
})
