import { StyleSheet, Text, Image, View, FlatList, Pressable } from 'react-native'
import React from 'react'
import { Feather } from '@expo/vector-icons';

const productData = [
    {
        name: "Casual T-Shrit",
        price: 100,
        imgUrl: 'https://images.pexels.com/photos/2043590/pexels-photo-2043590.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=1'
    },
    {
        name: "Casual T-Shrit",
        price: 110,
        imgUrl: 'https://images.pexels.com/photos/2043590/pexels-photo-2043590.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=1'
    },
    {
        name: "Casual T-Shrit",
        price: 120,
        imgUrl: 'https://images.pexels.com/photos/2043590/pexels-photo-2043590.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=1'
    },
    {
        name: "Casual T-Shrit",
        price: 130,
        imgUrl: 'https://images.pexels.com/photos/2043590/pexels-photo-2043590.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=1'
    },
    {
        name: "Casual T-Shrit",
        price: 140,
        imgUrl: 'https://images.pexels.com/photos/2043590/pexels-photo-2043590.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=1'
    },
    {
        name: "Casual T-Shrit",
        price: 150,
        imgUrl: 'https://images.pexels.com/photos/2043590/pexels-photo-2043590.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=1'
    },
    {
        name: "Casual T-Shrit",
        price: 160,
        imgUrl: 'https://images.pexels.com/photos/2043590/pexels-photo-2043590.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=1'
    },
    {
        name: "Casual T-Shrit",
        price: 170,
        imgUrl: 'https://images.pexels.com/photos/2043590/pexels-photo-2043590.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=1'
    },
    {
        name: "Casual T-Shrit",
        price: 180,
        imgUrl: 'https://images.pexels.com/photos/2043590/pexels-photo-2043590.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=1'
    },
    {
        name: "Casual T-Shrit",
        price: 190,
        imgUrl: 'https://images.pexels.com/photos/2043590/pexels-photo-2043590.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=1'
    }
]

const Item = () => {
    return (
        <FlatList
            data={productData}
            numColumns={2}
            contentContainerStyle={s.flatlistStyle}
            showsVerticalScrollIndicator={false}
            renderItem={({ item }) =>
                <Pressable style={s.itemWrapper}>
                    <Image style={s.image} source={{ uri: `${item.imgUrl}` }} />
                    <View style={s.textAndIconWrapper}>
                        <View>
                            <Text style={s.title}>{item.name}</Text>
                            <Text style={s.price}>{item.price}$</Text>
                        </View>
                        <Feather name="heart" size={24} color="#EC5668" />
                    </View>
                </Pressable>
            }
        />
    )
}

export default Item

const s = StyleSheet.create({
    flatlistStyle: {
        flex: 1,
        paddingTop: 10,
        paddingBottom: 150,
        justifyContent: 'space-between',
        backgroundColor: 'red',
        alignItems: 'center',
        rowGap: ''
    },
    itemWrapper: {
        height: 250,
        width: 170,
        borderRadius: 24,
        overflow: 'hidden'
    },
    image: {
        width: '100%',
        height: '80%',
        borderRadius: 24,
    },
    textAndIconWrapper: {
        flexDirection: 'row',
        justifyContent: "space-between",
        paddingHorizontal: 10,
        paddingTop: 5,
    },
    title: {
        fontSize: 16,
        fontWeight: '600',
        textTransform: 'uppercase'
    },
    price: {
        paddingLeft: 3,
        color: 'gray',
        fontSize: 13,
    }
})
