import { StyleSheet, Text, Image, View } from 'react-native'
import React from 'react'

const Ads = () => {
  return (
    <View style={styles.conatainer}>
      <View>
        <Image source={require('.')}/>
      </View>
      <View>
        <Text>Big Sale</Text>
        <Text>Get the trendy fashion at a discount up to 50%</Text>
      </View>
    </View>
  )
}

export default Ads

const styles = StyleSheet.create({
  conatainer: {
    height: 130,
    backgroundColor: '#E6606F',
    marginHorizontal: 24,
    borderRadius: 30,
  }
})
