    },
    {
        name: "Casual T-Shrit",
        price: 130,
        imgUrl: 'https://images.pexels.com/photos/2043590/pexels-photo-2043590.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=1'
    },
    {
        name: "Casual T-Shrit",
        price: 140,
        imgUrl: 'https://images.pexels.com/photos/2043590/pexels-photo-2043590.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=1'
    },
    {
        name: "Casual T-Shrit",
        price: 150,
        imgUrl: 'https://images.pexels.com/photos/2043590/pexels-photo-2043590.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=1'
    },
    {
        name: "Casual T-Shrit",
        price: 160,
        imgUrl: 'https://images.pexels.com/photos/2043590/pexels-photo-2043590.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=1'
    },
    {
        name: "Casual T-Shrit",
        price: 170,
        imgUrl: 'https://images.pexels.com/photos/2043590/pexels-photo-2043590.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=1'
    },
    {
        name: "Casual T-Shrit",
        price: 180,
        imgUrl: 'https://images.pexels.com/photos/2043590/pexels-photo-2043590.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=1'
    },
    {
        name: "Casual T-Shrit",
        price: 190,
        imgUrl: 'https://images.pexels.com/photos/2043590/pexels-photo-2043590.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=1'
    }
]

const Item = () => {
    return (
        <View style={s.conatiner}>
            <FlatList
                data={productData}
                numColumns={2}
                showsVerticalScrollIndicator={false}
                renderItem={({ item }) => { }}
            />
            
        </View>
    )
}

export default Item

const s = StyleSheet.create({
    conatiner: {
        paddingHorizontal: 24,
    },
    itemWrapper: {
        height: 250,
        width: 170,
        borderRadius: 24,
        overflow: 'hidden'
    },
    image: {
        width: '100%',
        height: '80%',
        borderRadius: 24,
    },
    textAndIconWrapper: {
        flexDirection: 'row',
        justifyContent: "space-between",
        paddingHorizontal: 10,
        paddingTop: 5,
    },
    title: {
        fontSize: 16,
        fontWeight: '600',
        textTransform: 'uppercase'
    },
    price: {
        paddingLeft: 3,
        color: 'gray',
        fontSize: 13,
    }
})
