import { StyleSheet, Text, Image, View, FlatList, Pressable } from 'react-native'
import React from 'react'
import { Feather } from '@expo/vector-icons';
import { useNavigation } from '@react-navigation/native';

const productData = [
    {
        id: 1,
        name: "T-Shrit",
        price: 100,
        imgUrl: 'https://images.pexels.com/photos/2043590/pexels-photo-2043590.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=1'
    },
    {
        id: 2,
        name: "Shorts",
        price: 110,
        imgUrl: 'https://images.pexels.com/photos/2690323/pexels-photo-2690323.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=1'
    },
    {
        id: 3,
        name: "Casual T-Shrit",
        price: 120,
        imgUrl: 'https://images.pexels.com/photos/1689731/pexels-photo-1689731.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=1'
    },
    {
        id: 4,
        name: "Casual T-Shrit",
        price: 130,
        imgUrl: 'https://images.pexels.com/photos/1755385/pexels-photo-1755385.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=1'
    },
    {
        id: 5,
        name: "T-Shrit",
        price: 140,
        imgUrl: 'https://images.pexels.com/photos/1081685/pexels-photo-1081685.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=1'
    },
    {
        id: 6,
        name: "Expensive T-Shrit",
        price: 150,
        imgUrl: 'https://images.pexels.com/photos/2043590/pexels-photo-2043590.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=1'
    },
    {
        id: 7,
        name: "Casual T-Shrit",
        price: 160,
        imgUrl: 'https://images.pexels.com/photos/2043590/pexels-photo-2043590.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=1'
    },
    {
        id: 8,
        name: "Casual T-Shrit",
        price: 170,
        imgUrl: 'https://images.pexels.com/photos/2043590/pexels-photo-2043590.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=1'
    },
    {
        id: 9,
        name: "Casual T-Shrit",
        price: 180,
        imgUrl: 'https://images.pexels.com/photos/2043590/pexels-photo-2043590.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=1'
    },
    {
        id: 10,
        name: "Casual T-Shrit",
        price: 190,
        imgUrl: 'https://images.pexels.com/photos/2043590/pexels-photo-2043590.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=1'
    }
]

const Item = () => {
    const navigation = useNavigation();
    return (
        <FlatList
            data={productData}
            contentContainerStyle={s.flatlistStyle}
            showsVerticalScrollIndicator={false}
            renderItem={({ item }) =>
                <Pressable onPress={() => {}} key={item.id} style={s.itemWrapper}>
                    <Image style={s.image} source={{ uri: `${item.imgUrl}` }} />
                    <View style={s.textAndIconWrapper}>
                        <View>
                            <Text style={s.title}>{item.name}</Text>
                            <Text style={s.price}>{item.price}$</Text>
                        </View>
                        <Feather name="heart" size={24} color="#EC5668" />
                    </View>
                </Pressable>
            }
        />
    )
}

export default Item

const s = StyleSheet.create({
    flatlistStyle: {
        flex: 1,
        paddingTop: 10,
        paddingBottom: 150,
        flexDirection: 'row',
        flexWrap: 'wrap',
        paddingHorizontal: 24,
        justifyContent: "space-between"
    },
    itemWrapper: {
        height: 250,
        width: 170,
        borderRadius: 24,
        overflow: 'hidden',
        marginBottom: 20,
    },
    image: {
        width: '100%',
        height: '80%',
        borderRadius: 24,
    },
    textAndIconWrapper: {
        flexDirection: 'row',
        justifyContent: "space-between",
        paddingHorizontal: 10,
        paddingTop: 5,
    },
    title: {
        fontSize: 14,
        fontWeight: '600',
        textTransform: 'uppercase'
    },
    price: {
        paddingLeft: 3,
        color: 'gray',
        fontSize: 13,
    }
})
